import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

class Login extends Component {
  state = {
    username: '',
    password: '',
    error: false,
  };

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value, error: false });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { username, password } = this.state;

    // Replace this block with your actual authentication logic
    // For simplicity, we assume a hardcoded username and password here
    const validUsername = 'sharmi';
    const validPassword = 'sharmi';

    if (username === validUsername && password === validPassword) {
      // Redirect to the home page after successful login
      this.props.history.push('/home');
    } else {
      // If login fails, show an error message
      this.setState({ error: true });
    }
  };

  render() {
    const { username, password, error } = this.state;

    return (
      <div>
        <div className="container">
          <div id="main-outer-div">
            <div id="title-div">
              <h4 className="title">Login in</h4>
            </div>

            <div id="outer-login-form-div">
              <form onSubmit={this.handleSubmit}>
                <input
                  className="login-form-input"
                  type="text"
                  placeholder="Username"
                  required
                  name="username"
                  value={username}
                  onChange={this.handleChange}
                />

                <input
                  className="login-form-input"
                  type="password"
                  placeholder="Password"
                  required
                  name="password"
                  value={password}
                  onChange={this.handleChange}
                />

                <input
                  className="login-form-input"
                  type="submit"
                  value="Login"
                  id="submitBtn"
                />
              </form>
              {error && <p className="error-message">Invalid username or password</p>}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(Login);
